class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        int n = s.size();
        if (!n) {
            return 0;
        }
        unordered_map&lt;char, int&gt; cnt;
        int l = 0, r = 1;
        int ans = 1;
        cnt[s[0]]++;
        while (r &lt; n) {
            while (r &lt; n &amp;&amp; cnt[s[r]] == 0) {
                cnt[s[r]]++;
                r++;
            }
            ans = max(r - l, ans);
            while (l &lt; r &amp;&amp; cnt[s[r]] != 0) {
                cnt[s[l]]--;
                l++;
            }
            if (r &lt; n) {
                cnt[s[r]]++;
                r++;
            }
        }

        return ans;
    }
};
438. 找到字符串中所有字母异位词
题意
