代码
class Solution {
public:
    vector&lt;int&gt; findAnagrams(string s, string p) {
        if (s.size() &lt; p.size() || s.empty() || p.empty()) {
            return {};
        }
        vector&lt;int&gt; ts(26, 0), tp(26, 0);
        int n = s.size(), m = p.size();
        for (int i = 0; i &lt; m; i++) {
            ts[s[i] - &#039;a&#039;]++, tp[p[i] - &#039;a&#039;]++;
        }
        int mc = 0;
        for (int i = 0; i &lt; 26; i++) {
            mc += tp[i] == ts[i];
        }
        int l = 0, r = m;
        vector&lt;int&gt; pos;
        while (r &lt;= n) {
            if (mc == 26) {
                pos.push_back(l);
            }
            if (r == n)
                break;
            mc -= tp[s[l] - &#039;a&#039;] == ts[s[l] - &#039;a&#039;];
            mc -= tp[s[r] - &#039;a&#039;] == ts[s[r] - &#039;a&#039;];
            ts[s[l] - &#039;a&#039;]--, ts[s[r] - &#039;a&#039;]++;
            mc += tp[s[l] - &#039;a&#039;] == ts[s[l] - &#039;a&#039;];
            mc += tp[s[r] - &#039;a&#039;] == ts[s[r] - &#039;a&#039;];
            l++, r++;
        }

        return pos;
    }
};
