代码
class Solution {
public:
    string minWindow(string s, string t) {
        int n = s.size(), m = t.size();
        if (n &lt; m) {
            return &quot;&quot;;
        }
        vector&lt;int&gt; mc(128, 0), cur(128, 0);
        for (int i = 0; i &lt; m; i++) {
            mc[t[i]]++, cur[s[i]]++;
        }

        int tot = 0;

        for (char c = &#039;A&#039;; c &lt;= &#039;Z&#039;; c++) {
            if (mc[c] &lt;= cur[c]) {
                tot++;
            }
        }
        for (char c = &#039;a&#039;; c &lt;= &#039;z&#039;; c++) {
            if (mc[c] &lt;= cur[c]) {
                tot++;
            }
        }

        int L = 0, R = 0;
        string ans = &quot;&quot;;
        int l = 0, r = m;
        if (tot == 52) {
            if (R == 0 || r - l &lt; R - L) {
                L = l, R = r;
            }
        }
        while (r &lt; n) {
            while (tot != 52 &amp;&amp; r &lt; n) {
                tot -= cur[s[r]] &gt;= mc[s[r]];
                cur[s[r]]++;
                tot += cur[s[r]] &gt;= mc[s[r]];
                r++;
            }
            while (l &lt; r &amp;&amp; mc[s[l]] &lt; cur[s[l]]) {
                tot -= cur[s[l]] &gt;= mc[s[l]];
                cur[s[l]]--;
                tot += cur[s[l]] &gt;= mc[s[l]];
                l++;
            }
            if (tot == 52) {
                if (R == 0 || r - l &lt; R - L) {
                    L = l, R = r;
                }
            }
            tot -= cur[s[l]] &gt;= mc[s[l]];
            cur[s[l]]--;
            tot += cur[s[l]] &gt;= mc[s[l]];
            l++;
            while (l &lt; r &amp;&amp; mc[s[l]] == 0) {
                tot -= cur[s[l]] &gt;= mc[s[l]];
                cur[s[l]]--;
                tot += cur[s[l]] &gt;= mc[s[l]];
                l++;
            }
        }

        return s.substr(L, R - L);
    }
};
