#include&lt;bits/stdc++.h&gt;
using namespace std;
void hanoi(int n, int from, int to) {
    if (n == 0) return;     // 全部移完，终止递归
    // 把from位置上的上面的n-1个移到不是from也不是to的位置
    hanoi(n - 1, from, (3 - from - to));
