    cout &lt;&lt; char(from + &#039;A&#039;) &lt;&lt; &quot;--&gt;&quot; &lt;&lt; char(to + &#039;A&#039;) &lt;&lt; endl;
    // 把之前移走的n-1个移到to位置
    hanoi(n - 1, (3 - from - to), to);
}
