void tocpyRec(int deg, int tox, int toy,int fillblank) {
    int len = pow(3, deg - 1);
    // 左上角标记
    for (int i = 0;i &lt; len;i++) {
        for (int j = 0;j &lt; len;j++) {
            // cout &lt;&lt; mp[i][j] &lt;&lt; &quot;:&quot; &lt;&lt; i &lt;&lt; &quot;-&quot; &lt;&lt; j &lt;&lt; endl;
            if (fillblank)
                mp[tox * len + i][toy * len + j] = &#039; &#039;;
            else
                mp[tox * len + i][toy * len + j] = mp[i][j];
        }
    }
}

void fw(int deg) {
    if (deg &gt; 1) {
        fw(deg - 1);
    }
    for (int i = 0;i &lt; 3;i++) {
        for (int j = 0;j &lt; 3;j++) {
            if ((i + j) % 2 == 0 &amp;&amp; (i || j)) {
                // 有内容的块坐标
                tocpyRec(deg - 1, i, j, 0);
            }
            else if (i || j) {
                tocpyRec(deg - 1, i, j, 1);
            }
        }
    }
}

int main() {
    mp[0][0] = &#039;X&#039;;
    int n;
    int flag = 0;
    while (cin &gt;&gt; n &amp;&amp; n != -1) {
        if (flag)cout &lt;&lt; &quot;-\n&quot;;
        flag = 1;
        fw(n);
        int len = pow(3, n - 1);
        for (int i = 0;i &lt; len;i++) 
            for (int j = 0;j &lt; len;j++) 
                if (mp[i][j] == &#039;X&#039;)
                    xpos[i] = j;

        for (int i = 0;i &lt; len;i++) {
            for (int j = 0;j &lt;= xpos[i];j++) {
                cout &lt;&lt; mp[i][j];
            }
            cout &lt;&lt; endl;
        }
    }

    return 0;
}

1247: A007 二叉树
题目描述
如图所示，由正整数1, 2, 3, …组成了一棵无限大的二叉树。从某一个结点到根结点（编号是1的结点）都有一条唯一的路径，比如从10到根结点的路径是(10, 5, 2, 1)，从4到根结点的路径是(4, 2, 1)，从根结点1到根结点的路径上只包含一个结点1，因此路径就是(1)。对于两个结点x和y，假设他们到根结点的路径分别是(x1, x2, … ,1)和(y1, y2, … ,1)（这里显然有x = x1，y = y1），那么必然存在两个正整数i和j，使得从xi 和 yj开始，有xi = yj , xi + 1 = yj + 1, xi + 2 = yj + 2,… 现在的问题就是，给定x和y，要求xi（也就是yj）。

输入
输入有多行，每行包括两个正整数x和y，这两个正整数都不大于1000。
输出
每行输出只有一个正整数xi。
样例输入
10 4
样例输出
2
参考代码
#include&lt;bits/stdc++.h&gt;
using namespace std;
const int N = 12;
int ax[N], ay[N];
int main() {
    int x, y;
    // 找重合的路径
    while (cin &gt;&gt; x &gt;&gt; y) {
        int kx = 0;
        while (x) {
            ax[kx++] = x % 2;
            x /= 2;
        }
        int ky = 0;
        while (y) {
            ay[ky++] = y % 2;
            y /= 2;
        }
        int kmii = kx &gt; ky ? ky : kx;
        int ans = 0;
        for (int i = 1;i &lt;= kmii;i++) {
            if (ax[kx - i] == ay[ky - i]) {
                ans &lt;&lt;= 1;
                ans += ax[kx - i];
            }
            else break;
        }
        cout &lt;&lt; ans &lt;&lt; endl;
    }

    return 0;
}

