#include&lt;bits/stdc++.h&gt;
using namespace std;
const int N = 60;
int wall[N][N], fw[N][N];
int roomnum = 0, sizea = 0;
void dfs(int x, int y) {
    // 查找点(x,y)的信息
    if (fw[x][y]) return;
    sizea++;
    fw[x][y] = roomnum;
    if ((wall[x][y] &amp; 1) == 0)dfs(x, y - 1);
    if ((wall[x][y] &amp; 2) == 0)dfs(x - 1, y);
    if ((wall[x][y] &amp; 4) == 0)dfs(x, y + 1);
    if ((wall[x][y] &amp; 8) == 0)dfs(x + 1, y);
}

int main() {
    int h, w;cin &gt;&gt; h &gt;&gt; w;
    for (int i = 1;i &lt;= h;i++){
        for (int j = 1;j &lt;= w;j++){
            cin &gt;&gt; wall[i][j];
        }
    }
    int maxroom = 0;
    for (int i = 1;i &lt;= h;i++) {
        for (int j = 1;j &lt;= w;j++) {
            if (!fw[i][j]) {
                // (i,j)没被找过(新的area)
                roomnum++;
                dfs(i, j);
                maxroom = max(maxroom, sizea);
                sizea = 0;
            }
        }
    }
    cout &lt;&lt; roomnum &lt;&lt; endl &lt;&lt; maxroom &lt;&lt; endl;

    return 0;
}

