4
参考代码
#include&lt;bits/stdc++.h&gt;
using namespace std;
typedef long long ll;
const int maxn = 150;

int n, m, k;
bool fd[maxn][maxn];
int mv[4][2] = { {1, 0}, {-1, 0}, {0, 1}, {0, -1} };

int ans;
int dfs(int r, int c) {
    if (r&lt;1 || r&gt;n || c&lt;1 || c&gt;m || !fd[r][c])return 0;
    fd[r][c] = false;
    int ret = 1;
    for (int i = 0;i &lt; 4;i++) {
        ret += dfs(r + mv[i][0], c + mv[i][1]);
    }
    return ret;
}

void solve() {
    fill(fd[0], fd[0] + maxn * maxn, false);
    ans = 0;
    for (int ki = 0;ki &lt; k;ki++) {
        int r, c;cin &gt;&gt; r &gt;&gt; c;
        fd[r][c] = true;
    }
    for (int i = 1;i &lt;= n;i++) {
        for (int j = 1;j &lt;= m;j++) {
            if (fd[i][j]) {
                ans = max(ans, dfs(i, j));
            }
        }
    }
    cout &lt;&lt; ans &lt;&lt; &quot;\n&quot;;
}

int main() {
    while (cin &gt;&gt; n &gt;&gt; m &gt;&gt; k)
        solve();

    return 0;
}
