参考代码
#include&lt;bits/stdc++.h&gt;
using namespace std;
const int maxn = 80;

int n, a[maxn], maxv, tot, vis[maxn], k, len;

bool cmp(int a, int b) {
    return a &gt; b;
}
bool dfs(int i, int rest, int p) {
    if (i &gt; k)
        return 1;
    int fail = 0;
    for (int x = p + 1;x &lt;= n;x++) {
        if (!vis[x]) {
            if (a[x] == a[fail]) continue;
            if (rest &gt; a[x]) {
                vis[x] = 1;
                bool w = dfs(i, rest - a[x], x);
                vis[x] = 0;
                if (!w) fail = x;
                if (w) return 1;
            }
            else if (rest == a[x]) {
                vis[x] = 1;
                bool w = dfs(i + 1, len, 0);
                vis[x] = 0;
                return w;
            }
            if (p == 0) return 0;
        }
    }
    return 0;
}

void solve() {
    fill(vis, vis + n + 5, 0);
    maxv = tot = 0;
    for (int i = 1;i &lt;= n;i++) {
        cin &gt;&gt; a[i];
        maxv = max(maxv, a[i]);
        tot += a[i];
    }
    sort(a + 1, a + 1 + n, cmp);
    for (len = maxv;len &lt;= tot;len++) {
        if (tot % len == 0) {
            k = tot / len;
            if (dfs(1, len, 0)) {
                cout &lt;&lt; len &lt;&lt; endl;
                break;
            }
        }
    }
}

int main() {
    while (cin &gt;&gt; n &amp;&amp; n)
        solve();

    return 0;
}
