    while (cnt &lt; n) {
        ans++;
        int ki = 0;
        int li = 0, wi = 0;
        for (int i = 0;i &lt; n;i++) {
            if (!ck[i]) {
                ki = i;
                li = len[i].first, wi = len[i].second;
