参考代码
#include&lt;bits/stdc++.h&gt;
using namespace std;
typedef long long ll;

struct node {
    double x, y;
    node(double x, double y) :x(x), y(y) {}
    bool operator &lt; (const node&amp; a) const {
        return y &lt; a.y;
    }
};
int n;
int d;
int ind;
void solve() {
    ind = 1;
    while (cin &gt;&gt; n &gt;&gt; d) {
        if (n == 0 &amp;&amp; d == 0)return;
        bool f = true;
        vector&lt;node&gt; q;
        for (int i = 0;i &lt; n;i++) {
            int x, y;cin &gt;&gt; x &gt;&gt; y;
            if (d &lt; y)
                f = false;
            if (!f)continue;
            double ei = sqrt(1.0 * d * d - 1.0 * y * y);
            q.push_back(node{ x - ei,x + ei });
        }
        if (!f) {
            printf(&quot;Case %d: -1\n&quot;, ind);
