v1((v1))-.3.-&gt;v3((v3))
v1((v1))-.8.-&gt;v4((v4))
v2((v2))-.2.-&gt;v6((v6))
v7((v7))-.5.-&gt;v6((v6))
v7((v7))-.1.-&gt;v4((v4))
v3((v3))-.1.-&gt;v7((v7))
v4((v4))-.5.-&gt;v3((v3))
v4((v4))-.5.-&gt;v5((v5))

