.terms-tags a {
    --size: 4;
    font-size: calc(var(--size) * 0.25rem + 1rem);

    /* font-size: 1.4rem; */
    display: block;
    padding: 3px 10px;
    background: var(--tertiary);
    border-radius: 6px;
    transition: transform 0.1s;
}

